import { createSlice } from "@reduxjs/toolkit";

export const topicsSlice = createSlice({
  name: "topics",
  initialState: {
    topics: {},
    quizzes: {},
  },
  reducers: {
    addTopic: (state, action) => {
      state.topics[action.payload.id] = { ...action.payload, quizIds: [] };
    },
    addQuizId: (state, action) => {
      console.log("Full Proxy State:");
      console.log(state); // Shows Proxy object

      console.log("Extracted State (Using JSON):");
      console.log(JSON.parse(JSON.stringify(state))); // Converts to a readable object

      console.log("Topics Object:");
      console.log(state.topics); // Logs the topics directly
    },
  },
});

export const selectTopics = (state) => state.topics.topics;
export const { addTopic, addQuizId } = topicsSlice.actions;
export default topicsSlice.reducer;
