import { createSlice } from "@reduxjs/toolkit";

export const quizzesSlice = createSlice({
  name: "quizzes",
  initialState: {
    quizzes: {},
  },
  reducers: {
    addQuiz: (state, action) => {
      console.log("Full Proxy State:");
      console.log(state); // Shows Proxy object

      console.log("Extracted State (Using JSON):");
      console.log(JSON.parse(JSON.stringify(state))); // Converts to a readable object

      console.log("Quiz Object:");
      console.log(state.quizzes); // Logs the topics directly
      state.quizzes = {
        [action.payload.id]: action.payload,
      };
    },
  },
});

export const selectQuizzes = (state) => state.quizzes.quizzes;
export const { addQuiz } = quizzesSlice.actions;
export default quizzesSlice.reducer;
